        public const string GetBalance = "query GetBalance($spaceId: String!, $itemId: String!) {\n  game(spaceId: $spaceId) {\n    id\n    viewer {\n      meta {\n        id\n        secondaryStoreItem(itemId: $itemId) {\n          meta {\n            id\n            quantity\n            __typename\n          }\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n}";
        public const string GetPlayerProfile = "query GetPlayerProfile($spaceId: String!, $privilegeIds: [String!]!) {\n  viewer {\n    id\n    is2FActive\n    country\n    name\n    game(spaceId: $spaceId) {\n      meta {\n        id\n        isOwned\n        ...PlayerPrivilegeFragment\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n}\n\nfragment PlayerPrivilegeFragment on UserGameMeta {\n  id\n  privileges(privilegeIds: $privilegeIds) {\n    id\n    privilegeId\n    isGranted\n    reason {\n      id\n      name\n      reasonId\n      description\n      __typename\n    }\n    __typename\n  }\n  __typename\n}";
        public const string GetMarketableItems = "query GetMarketableItems($spaceId: String!, $limit: Int!, $offset: Int, $filterBy: MarketableItemFilter, $withOwnership: Boolean = true, $sortBy: MarketableItemSort) {\n  game(spaceId: $spaceId) {\n    id\n    marketableItems(\n      limit: $limit\n      offset: $offset\n      filterBy: $filterBy\n      sortBy: $sortBy\n      withMarketData: true\n    ) {\n      nodes {\n        ...MarketableItemFragment\n        ...MarketableItemActiveBuyTradesFragment\n        __typename\n      }\n      totalCount\n      __typename\n    }\n    __typename\n  }\n}\n\nfragment MarketableItemFragment on MarketableItem {\n  id\n  item {\n    ...SecondaryStoreItemFragment\n    ...SecondaryStoreItemOwnershipFragment @include(if: $withOwnership)\n    __typename\n  }\n  marketData {\n    ...MarketDataFragment\n    __typename\n  }\n  __typename\n}\n\nfragment MarketableItemActiveBuyTradesFragment on MarketableItem {\n  viewer {\n    meta {\n      id\n      activeBuyTrades: trades(filterBy: {states: [Created], category: Buy}) {\n        nodes {\n          ...TradeFragment\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment SecondaryStoreItemFragment on SecondaryStoreItem {\n  id\n  assetUrl\n  itemId\n  name\n  description\n  subtitle\n  tags\n  type\n  maximumQuantity\n  __typename\n}\n\nfragment SecondaryStoreItemOwnershipFragment on SecondaryStoreItem {\n  id\n  viewer {\n    meta {\n      id\n      isOwned\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment MarketDataFragment on MarketableItemMarketData {\n  id\n  sellStats {\n    id\n    paymentItemId\n    lowestPrice\n    highestPrice\n    activeCount\n    __typename\n  }\n  buyStats {\n    id\n    paymentItemId\n    lowestPrice\n    highestPrice\n    activeCount\n    __typename\n  }\n  lastSoldAt {\n    id\n    paymentItemId\n    price\n    performedAt\n    __typename\n  }\n  __typename\n}\n\nfragment TradeFragment on Trade {\n  id\n  tradeId\n  state\n  category\n  createdAt\n  lastModifiedAt\n  daysBeforeExpiration\n  failures\n  tradeItems {\n    id\n    item {\n      ...SecondaryStoreItemFragment\n      ...SecondaryStoreItemOwnershipFragment\n      __typename\n    }\n    __typename\n  }\n  payment {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    transactionFee\n    __typename\n  }\n  paymentOptions {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    transactionFee\n    __typename\n  }\n  paymentProposal {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    __typename\n  }\n  viewer {\n    meta {\n      id\n      tradesLimitations {\n        ...TradesLimitationsFragment\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment SecondaryStoreItemQuantityFragment on SecondaryStoreItem {\n  id\n  viewer {\n    meta {\n      id\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment TradesLimitationsFragment on UserGameTradesLimitations {\n  id\n  buy {\n    id\n    resolvedTransactionCount\n    resolvedTransactionPeriodInMinutes\n    activeTransactionCount\n    __typename\n  }\n  sell {\n    id\n    resolvedTransactionCount\n    resolvedTransactionPeriodInMinutes\n    activeTransactionCount\n    resaleLocks {\n      itemId\n      expiresAt\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}"; 
        public const string GetSellableItems = "query GetSellableItems($spaceId: String!, $limit: Int!, $offset: Int, $filterBy: MarketableItemFilter, $withOwnership: Boolean = false, $sortBy: MarketableItemSort) {\n  game(spaceId: $spaceId) {\n    id\n    viewer {\n      meta {\n        id\n        marketableItems(\n          limit: $limit\n          offset: $offset\n          filterBy: $filterBy\n          sortBy: $sortBy\n          withMarketData: true\n        ) {\n          nodes {\n            ...MarketableItemFragment\n            __typename\n          }\n          totalCount\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n}\n\nfragment MarketableItemFragment on MarketableItem {\n  id\n  item {\n    ...SecondaryStoreItemFragment\n    ...SecondaryStoreItemOwnershipFragment @include(if: $withOwnership)\n    __typename\n  }\n  marketData {\n    ...MarketDataFragment\n    __typename\n  }\n  viewer {\n    meta {\n      id\n      activeTrade {\n        ...TradeFragment\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment SecondaryStoreItemFragment on SecondaryStoreItem {\n  id\n  assetUrl\n  itemId\n  name\n  description\n  subtitle\n  tags\n  type\n  maximumQuantity\n  __typename\n}\n\nfragment SecondaryStoreItemOwnershipFragment on SecondaryStoreItem {\n  id\n  viewer {\n    meta {\n      id\n      isOwned\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment MarketDataFragment on MarketableItemMarketData {\n  id\n  sellStats {\n    id\n    paymentItemId\n    lowestPrice\n    highestPrice\n    activeCount\n    __typename\n  }\n  buyStats {\n    id\n    paymentItemId\n    lowestPrice\n    highestPrice\n    activeCount\n    __typename\n  }\n  lastSoldAt {\n    id\n    paymentItemId\n    price\n    performedAt\n    __typename\n  }\n  __typename\n}\n\nfragment TradeFragment on Trade {\n  id\n  tradeId\n  state\n  category\n  createdAt\n  expiresAt\n  lastModifiedAt\n  failures\n  tradeItems {\n    id\n    item {\n      ...SecondaryStoreItemFragment\n      ...SecondaryStoreItemOwnershipFragment\n      __typename\n    }\n    __typename\n  }\n  payment {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    transactionFee\n    __typename\n  }\n  paymentOptions {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    transactionFee\n    __typename\n  }\n  paymentProposal {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    __typename\n  }\n  viewer {\n    meta {\n      id\n      tradesLimitations {\n        ...TradesLimitationsFragment\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment SecondaryStoreItemQuantityFragment on SecondaryStoreItem {\n  id\n  viewer {\n    meta {\n      id\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment TradesLimitationsFragment on UserGameTradesLimitations {\n  id\n  buy {\n    id\n    resolvedTransactionCount\n    resolvedTransactionPeriodInMinutes\n    activeTransactionCount\n    __typename\n  }\n  sell {\n    id\n    resolvedTransactionCount\n    resolvedTransactionPeriodInMinutes\n    activeTransactionCount\n    resaleLocks {\n      itemId\n      expiresAt\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}";
        public const string GetTransactionPending = "query GetTransactionsPending($spaceId: String!, $limit: Int!, $offset: Int) {\n  game(spaceId: $spaceId) {\n    id\n    viewer {\n      meta {\n        id\n        trades(\n          limit: $limit\n          offset: $offset\n          filterBy: {states: [Created]}\n          sortBy: {field: LAST_MODIFIED_AT}\n        ) {\n          nodes {\n            ...TradeFragment\n            __typename\n          }\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n}\n\nfragment TradeFragment on Trade {\n  id\n  tradeId\n  state\n  category\n  createdAt\n  expiresAt\n  lastModifiedAt\n  failures\n  tradeItems {\n    id\n    item {\n      ...SecondaryStoreItemFragment\n      ...SecondaryStoreItemOwnershipFragment\n      __typename\n    }\n    __typename\n  }\n  payment {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    transactionFee\n    __typename\n  }\n  paymentOptions {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    transactionFee\n    __typename\n  }\n  paymentProposal {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    __typename\n  }\n  viewer {\n    meta {\n      id\n      tradesLimitations {\n        ...TradesLimitationsFragment\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment SecondaryStoreItemFragment on SecondaryStoreItem {\n  id\n  assetUrl\n  itemId\n  name\n  tags\n  type\n  __typename\n}\n\nfragment SecondaryStoreItemOwnershipFragment on SecondaryStoreItem {\n  id\n  viewer {\n    meta {\n      id\n      isOwned\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment SecondaryStoreItemQuantityFragment on SecondaryStoreItem {\n  id\n  viewer {\n    meta {\n      id\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment TradesLimitationsFragment on UserGameTradesLimitations {\n  id\n  buy {\n    id\n    resolvedTransactionCount\n    resolvedTransactionPeriodInMinutes\n    activeTransactionCount\n    __typename\n  }\n  sell {\n    id\n    resolvedTransactionCount\n    resolvedTransactionPeriodInMinutes\n    activeTransactionCount\n    resaleLocks {\n      id\n      itemId\n      expiresAt\n      __typename\n    }\n    __typename\n  }\n  __typename\n}";
        public const string GetTransactionHistory = "query GetTransactionsHistory($spaceId: String!, $limit: Int!, $offset: Int) {\n  game(spaceId: $spaceId) {\n    id\n    viewer {\n      meta {\n        id\n        trades(\n          limit: $limit\n          offset: $offset\n          filterBy: {states: [Succeeded, Failed]}\n          sortBy: {field: LAST_MODIFIED_AT}\n        ) {\n          nodes {\n            ...TradeFragment\n            __typename\n          }\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n}\n\nfragment TradeFragment on Trade {\n  id\n  tradeId\n  state\n  category\n  createdAt\n  expiresAt\n  lastModifiedAt\n  failures\n  tradeItems {\n    id\n    item {\n      ...SecondaryStoreItemFragment\n      ...SecondaryStoreItemOwnershipFragment\n      __typename\n    }\n    __typename\n  }\n  payment {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    transactionFee\n    __typename\n  }\n  paymentOptions {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    transactionFee\n    __typename\n  }\n  paymentProposal {\n    id\n    item {\n      ...SecondaryStoreItemQuantityFragment\n      __typename\n    }\n    price\n    __typename\n  }\n  viewer {\n    meta {\n      id\n      tradesLimitations {\n        ...TradesLimitationsFragment\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment SecondaryStoreItemFragment on SecondaryStoreItem {\n  id\n  assetUrl\n  itemId\n  name\n  tags\n  type\n  __typename\n}\n\nfragment SecondaryStoreItemOwnershipFragment on SecondaryStoreItem {\n  id\n  viewer {\n    meta {\n      id\n      isOwned\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment SecondaryStoreItemQuantityFragment on SecondaryStoreItem {\n  id\n  viewer {\n    meta {\n      id\n      quantity\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment TradesLimitationsFragment on UserGameTradesLimitations {\n  id\n  buy {\n    id\n    resolvedTransactionCount\n    resolvedTransactionPeriodInMinutes\n    activeTransactionCount\n    __typename\n  }\n  sell {\n    id\n    resolvedTransactionCount\n    resolvedTransactionPeriodInMinutes\n    activeTransactionCount\n    resaleLocks {\n      id\n      itemId\n      expiresAt\n      __typename\n    }\n    __typename\n  }\n  __typename\n}";
        public const string GetMarketplaceGameConfig = "query GetMarketplaceGameConfig($spaceId: String!) {\n  game(spaceId: $spaceId) {\n    id\n    marketplace {\n      assets {\n        backgroundImage\n        homeAssets {\n          buyTab\n          sellTab\n          transactionsTab\n          __typename\n        }\n        __typename\n      }\n      privilege {\n        tradePrivilegeId\n        __typename\n      }\n      currency {\n        currencyId\n        currencyUrl\n        __typename\n      }\n      contact {\n        bugUrl\n        bugLabel\n        __typename\n      }\n      tags {\n        color\n        value\n        iconUrl\n        displayName\n        __typename\n      }\n      tagGroups {\n        type\n        values\n        displayName\n        __typename\n      }\n      types {\n        value\n        iconUrl\n        displayName\n        __typename\n      }\n      homeTypes {\n        value\n        displayName\n        iconUrl\n        __typename\n      }\n      filters {\n        categoryId\n        type\n        displayName\n        sortType\n        sortDirection\n        values {\n          value\n          displayName\n          __typename\n        }\n        __typename\n      }\n      recommendations {\n        userItemRecommendations {\n          projectId\n          categoryId\n          __typename\n        }\n        __typename\n      }\n      tabConfig {\n        buy {\n          filterSelection {\n            categoryId\n            values\n            __typename\n          }\n          sortSelection {\n            field\n            direction\n            orderType\n            __typename\n          }\n          __typename\n        }\n        sell {\n          filterSelection {\n            categoryId\n            values\n            __typename\n          }\n          sortSelection {\n            field\n            direction\n            orderType\n            __typename\n          }\n          __typename\n        }\n        __typename\n      }\n      userResearch {\n        link\n        linkId\n        homeBanner {\n          iconUrl\n          title\n          description\n          buttonLabel\n          __typename\n        }\n        footerLink {\n          iconUrl\n          buttonLabel\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n}";
        public const string GetTradesConfig = "query GetTradesConfig($spaceId: String!) {\n  game(spaceId: $spaceId) {\n    id\n    tradesConfig {\n      id\n      saleExpiresAfterMinutes\n      buySlots\n      sellSlots\n      buyLimit {\n        id\n        maximumCount\n        __typename\n      }\n      sellLimit {\n        id\n        maximumCount\n        __typename\n      }\n      resaleLockDurationInMinutes\n      transactionFeesConfig {\n        id\n        paymentItemId\n        feePercentage\n        __typename\n      }\n      twoFactorAuthenticationRule\n      gameOwnershipRule\n      __typename\n    }\n    __typename\n  }\n}";
